mapper.update:
update post
set comment_count = #{commentCount}
where id = #{postId}

update post
set status = #{status}
where id = #{postId}

update post
set score = #{score}
where id = #{postId}

mapper.select:
select 
from post
where status != 1

    and user_id = #{userId}

order by create_time desc
limit #{offset}, #{limit}
